import products from "../products.json";
import imagemTeste from "../images/products/impressora-canon-160-160.jpg";

function ShoppingCart() {
  let totalPrice = 0;
  products.cart.item.forEach((product) => {
    totalPrice += product.bestPrice;
  });
  const totalPriceFormated = (totalPrice / 100).toLocaleString("pt-br", {
    style: "currency",
    currency: "BRL",
  });

  return (
    <div className="shopping-cart">
      {products.cart.item.map((product, index) => (
        <div className="cart-item" key={index}>
          <img src={imagemTeste} alt="teste" className="img-item" />
          <div>
            <p className="name-item">{product.name}</p>
            <span className="qtd">Qtd.: {product.quantity}</span>
            <span className="price">{product.bestPriceFormated}</span>
          </div>
        </div>
      ))}
      <h3>Total do pedido: {totalPriceFormated}</h3>
      <button type="button" className="btn-finalize">
        FINALIZAR COMPRA
      </button>
    </div>
  );
}

export default ShoppingCart;
