import { useEffect, useState } from "react";

function ShoppingCart() {
  const calculateTotalPrice = (allProducts) => {
    let totalPrice = 0;
    allProducts.cart.item.forEach((product) => {
      totalPrice += product.bestPrice;
    });
    const totalPriceFormated = (totalPrice / 100).toLocaleString("pt-br", {
      style: "currency",
      currency: "BRL",
    });
    return totalPriceFormated;
  };

  const clickCart = () => {
    setOpen("open")
  }

  const [isLoading, setIsLoading] = useState(true);
  const [products, setProducts] = useState({});
  const [totalPrice, setTotalPrice] = useState("");
  const [open, setOpen] = useState("")

  useEffect(() => {
    fetch("https://products-teste.herokuapp.com/")
      .then((response) => response.json())
      .then((result) => {
        setProducts(result)
        setTotalPrice(calculateTotalPrice(result))
        setIsLoading(false)
      });
  }, []);

  return isLoading ? (
    <p className="shopping-cart">Loading...</p>
  ) : (
    <div className="shopping-cart">
      <div className="shopping-cart-body">
        {products.cart.item.map((product, index) => (
          <div className="cart-item" key={index}>
            {/* <img src={imagemTeste} alt="teste" className="img-item" /> */}
            <div>
              <p className="name-item">{product.name}</p>
              <span className="qtd">Qtd.: {product.quantity}</span>
              <span className="price">{product.bestPriceFormated}</span>
            </div>
          </div>
        ))}
      </div>
      <div className="shopping-cart-footer">
        <h3>Total do pedido: {totalPrice}</h3>
      </div>
      <div className="shopping-cart-botton">
        <button type="button" className="btn-finalize">
          FINALIZAR COMPRA
        </button>
      </div>
    </div>
  );
}

export default ShoppingCart;
